import React, { useState } from 'react'
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs'
import { NavigationContainer } from '@react-navigation/native'
import { createStackNavigator } from '@react-navigation/stack';
import { Entypo, Feather } from '@expo/vector-icons'

import Home from './components/home'
import Criar from './components/create'
import Ler from './components/read'
import Atualizar from './components/update'
import Apagar from './components/delete'
import Login from './components/login'
import NewUser from './components/newUser'
import Register from './components/register'
import Delete from './components/delete'
const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

function Tabs({ route }) {
  var param = route.params?.idUser
  return (
    <Tab.Navigator
      screenOptions={{
        tabBarStyle: {
          backgroundColor: '#121212',
          borderTopColor: 'tranparent',
          paddingBottom: 5,
          paddingTop: 5,
        },
        tabBarActiveTintColor: '#fff',
        tabBarInactiveTintColor: '#555'
      }}
    >

      <Tab.Screen name='Create' component={Criar} initialParams={{ 'iddocara': param }}
        options={{
          headerShown: false,
          tabBarIcon: ({ size, color }) => (
            <Entypo name="news" size={size} color={color} />
          )
        }}
      />

      <Tab.Screen name='Read' component={Ler}
        options={{
          headerShown: false,
          tabBarIcon: ({ size, color }) => (
            <Feather name="search" size={size} color={color} />
          )
        }}
      />

      <Tab.Screen name='Update' component={Atualizar}
        options={{
          headerShown: false,
          tabBarIcon: ({ size, color }) => (
            <Feather name="save" size={size} color={color} />
          )
        }}
      />

      <Tab.Screen name='Delete' component={Apagar}
        options={{
          headerShown: false,
          tabBarIcon: ({ size, color }) => (
            <Feather name="delete" size={size} color={color} />
          )
        }}
      />
    </Tab.Navigator>
  )
}

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen
          name='Home'
          component={Home}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='Login'
          component={Login}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='NewUser'
          component={NewUser}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='TabBar'
          component={Tabs} //Tabs = Chama toda a TabBar
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='Register'
          component={Register}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='Delete'
          component={Delete}
          options={{ headerShown: false }}
        />

        <Stack.Screen
          name='Image'
          component={Image}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='Create'
          component={Criar}
          options={{ headerShown: false }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  )
}
