import React, { Component } from "react";
import { TouchableOpacity, StyleSheet, View, ImageBackground, Text } from "react-native";
import FontAwesomeIcon from "react-native-vector-icons/FontAwesome";
import MaterialUnderlineTextbox from "../components/MaterialUnderlineTextbox";
import IoniconsIcon from "react-native-vector-icons/Ionicons";
import MaterialButtonViolet from "../components/MaterialButtonViolet";
import myImage from "../assets/images/Gradient_DpxnLje.png";
import "../styles.css";

function Untitled({props, navigation}) {
  return (
    <View style={styles.container}>
      <ImageBackground
        style={styles.rect}
        imageStyle={styles.rect_imageStyle}
        source={myImage}
      >
        <Text style={styles.mangym}>MANGYM</Text>
        <View style={styles.iconRow}>
          <FontAwesomeIcon name="user-o" style={styles.icon}></FontAwesomeIcon>
          <MaterialUnderlineTextbox
            inputStyle="User"
            style={styles.materialUnderlineTextbox2}
          ></MaterialUnderlineTextbox>
        </View>
        <View style={styles.icon2Row}>
          <FontAwesomeIcon name="lock" style={styles.icon2}></FontAwesomeIcon>
          <MaterialUnderlineTextbox
            inputStyle="Password"
            style={styles.materialUnderlineTextbox1}
          ></MaterialUnderlineTextbox>
        </View>
        <MaterialButtonViolet
          caption="Login"
          style={styles.materialButtonViolet}
        ></MaterialButtonViolet>
        <Text style={styles.loremIpsum}>Forgot your password?</Text>
        <View>
        <TouchableOpacity style={styles.button1} onPress={()=>navigation.navigate("Create")}>SIGN UP</TouchableOpacity>
      </View>
      </ImageBackground>
     
    </View>
    
  );
}

const styles = StyleSheet.create({
  button1: {
    marginTop: 32,
    left: 97,
    width: 187,
    height: 49,
