import React, { Component, useState, useEffect } from "react";
import { TouchableOpacity, StyleSheet, View, ImageBackground, Text, TextInput, KeyboardAvoidingView, PlatformWebStatic} from "react-native";
import FontAwesomeIcon from "react-native-vector-icons/FontAwesome";
import MaterialUnderlineTextbox from "./loginFolder/MaterialUnderlineTextbox";
import IoniconsIcon from "react-native-vector-icons/Ionicons";
import MaterialButtonViolet from "./loginFolder/MaterialButtonViolet";
import myImage from "../assets/images/Gradient_DpxnLje.png";
import firebaseApp from './firebaseConfig';
import { MaterialCommunityIcons } from '@expo/vector-icons'


import "../styles.css";



function Login({props, navigation}) {
  const [email, setEmail] = useState('')
      const [password, setPassword] = useState('')
      const [errorLogin, setErrorLogin] = useState('')
  
      //https://firebase.google.com/docs/auth/web/password-auth
      const loginFirebase = () => {
          firebaseApp.auth().signInWithEmailAndPassword(email, password)
              .then((userCredential) => {
                  let user = userCredential.user;
                  ///navigation.navigate('Create', { idUser: user.uid })
                  navigation.navigate('TabBar', { idUser: user.uid })
              })
              .catch((error) => {
                  setErrorLogin(true)
                  let errorCode = error.code
                  let errorMessage = error.message
              });
      }
  
      useEffect(() => {
          // firebaseApp.auth().onAuthStateChanged(function (user){
          //     if (user) {
          //      navigation.navigate('TabBar',{idUser: user.uid})   
          //     }
              
          // })
      }, []);
  
  return (
    <View style={styles.container}>
      <ImageBackground
        style={styles.rect}
        imageStyle={styles.rect_imageStyle}
        source={myImage}
      >                                                                                                                              
        <Text style={styles.mangym}>MANGYM</Text>
        <View style={styles.iconRow}>
          <FontAwesomeIcon name="user-o" style={styles.icon}></FontAwesomeIcon>
          <MaterialUnderlineTextbox
            inputStyle="User"
            style={styles.materialUnderlineTextbox2}
             onChangeText={(text) => setEmail(text)}
                    value={email}
          ></MaterialUnderlineTextbox>
        </View>
        <View style={styles.icon2Row}>
          <FontAwesomeIcon name="lock" style={styles.icon2}></FontAwesomeIcon>
          <MaterialUnderlineTextbox
            inputStyle="Password"
            style={styles.materialUnderlineTextbox1}
            secureTextEntry={true}
            type='password'
            onChangeText={(text) => setPassword(text)}
            value={password}
          ></MaterialUnderlineTextbox>
        </View>
        <MaterialButtonViolet
          caption="Login"
          style={styles.materialButtonViolet}
          onPress={loginFirebase}
        ></MaterialButtonViolet>
        {/* ############ ERROR #######################*/}
         {errorLogin === true
            ?
            <View style={styles.error}>
                <MaterialCommunityIcons
                    name='alert-circle'
                    size={20}
                    color='#f00'
                /><Text style={styles.warning}>E-mail o senha inválido...</Text>
            </View>
            :
            <View />
        }
        {/*############ FIM ERROR ################## */}

        {email === "" || password === ""
            ?
            <TouchableOpacity
                disabled={true}
                style={styles.buttonLogin}
            >
            </TouchableOpacity>
            :
            <TouchableOpacity style={styles.buttonLogin}
                onPress={loginFirebase}
                
            >
                <Text style={styles.textLoginOn}>Login</Text>
            </TouchableOpacity>
        }
        
        <View>
        <TouchableOpacity style={styles.button1} onPress={()=>navigation.navigate("Register")}>Register</TouchableOpacity>
      </View>
      </ImageBackground>
     
    </View>
    
  );
}

