import React, { useState } from 'react'
import { View, Text, StyleSheet, TextInput, Button } from 'react-native'
import database from './firebaseConfig'

export default function Update({ navigation, route }) {
  const[descriptionEdit, setDescriptionEdit] = useState(route.params.description)
  const idDelete=route.params.id

  function editar(description, id){
    database.collection('Crud').doc(id).update({
      description: descriptionEdit
    })
  }

  return (
    <View style={styles.container}>
      <Text style={styles.titleText}>Create</Text>
      <TextInput
        style={styles.textInput}
        placeholder="Nome do curso"
        onChangeText={setDescriptionEdit}//##########
        value={descriptionEdit}//##########
      />  
      <View style={styles.textSucesso}>
        <Text style={{color:'#0c0', fontSize:20}}>X</Text>
      </View>
      <View style={styles.botaoSalvar}>
        <Button
          title='Salvar'
          onPress={() => {
            editar(descriptionEdit, idDelete)//##########
          }}
        />
      </View>
    </View >
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
  },
  textInput: {
    borderRadius: 5,
    backgroundColor: '#eee',
    marginTop: 10,
    padding: 10,
    height: 40,
    width:'60%',
  },
  botaoSalvar: {
    marginBottom:30,
  },
  salvar: {
    fontSize: 15,
    color: '#fff',
    fontFamily:'arial',
  },
  titleText:{
    color:'#f20a4f',
    fontSize:25, 
    fontWeight:'bold',
    fontFamily:'calibri'
  },
  textSucesso:{
    flex:1,
    //backgroundColor:'#555',
    alignItems:'center',
    justifyContent:'flex-end',
    width:'90%',
    marginBottom:10,
    marginTop:'95%',
  }
})
