      setUsuario("");
      setNome("");
      setIdade("");
      setNomeE("");
      setIdadeE("");
      setImage("")
    } else {


      for (let i = 0; i < clientes.length; i++) {

        if (clientes[i].nome.match(nome)) {

          setUsuario(clientes[i]);
          setNome(clientes[i].nome);
          setIdade(clientes[i].idade);
          setNomeE(clientes[i].nome);
          setIdadeE(clientes[i].idade);
          setImage(clientes[i].image)
          verify = true;
          break;

        }
      }

      if (verify == false) {
        alert("Usuário não encontrado!")
        setUsuario("");
        setNome("");
        setIdade("");
        setNomeE("");
        setIdadeE("");
        setImage("")
      }

    }

  }

  useEffect(() => {

    db.collection("mangym").onSnapshot((query) => {

      const list = []
      query.forEach((doc) => {
        list.push({ ...doc.data(), id: doc.id })
      })
      setClientes(list)
    })


  }, [])

  return (
    <View style={styles.container}>
